mapper.delete:
delete from

where id=#{id} 

	and userId  =#{userId}

mapper.select:
	select t.*,c.typeName from
	tally  t left JOIN  consumetype c on t.type=c.id WHERE 1=1

		and t.id = #{id}


		and t.userId = #{userId}


	 AND t.createDate  >= CONCAT( #{createDate}, ' 00:00:00' ) 
     AND t.createDate <= CONCAT( #{createDate}, ' 23:59:59' ) 


		and t.used like concat('%',#{used},'%')


		and t.howMuch = #{howMuch}


		and t.userId  =#{userId}


		and t.type  =#{type}


	 AND t.createDate  >= CONCAT( #{startDate}, ' 00:00:00' ) 
     AND t.createDate <= CONCAT( #{endDate}, ' 23:59:59' ) 

	order by t.createDate desc

SELECT CONVERT ( SUM( t.howMuch ), DECIMAL ( 12, 2 ) ) AS howMuch, t.type, c.typeName FROM tally t LEFT JOIN consumetype c ON t.type = c.id WHERE t.createDate >= CONCAT( #{startTime}, ' 00:00:00' ) and t.createDate <= CONCAT( #{endTime}, ' 23:59:59' ) and t.userId=#{userId}
and t.type =#{type}
GROUP BY t.type , c.typeName ORDER BY t.type ASC
